import React, { useState } from "react";

const EduItem = ({ education }) => {
  const [showDetails, setShowDetails] = useState(false);
  const [showCS, setShowCS] = useState(false);
  const [showMath, setShowMath] = useState(false);

  return (
    <div className="edu-item">
      <div
        className="edu-title cursor"
        onClick={() => setShowDetails(!showDetails)}
      >
        {education.title}
      </div>
      <div className="edu-dates">
        <span className="edu-start">{education.start}</span>
        {" - "}
        <span className="edu-end">{education.end}</span>
      </div>
      {showDetails ? (
        <div className="edu-details">
          {education.csCourses ? (
            <ul
              className="edu-cs cursor edu-ul"
              onClick={() => setShowCS(!showCS)}
            >
              Computer Science courses
              {showCS
                ? education.csCourses.map((c) => {
                    return <li>{c}</li>;
                  })
                : null}
            </ul>
          ) : null}
          {education.mathCourses ? (
            <ul
              className="edu-math cursor edu-ul"
              onClick={() => setShowMath(!showMath)}
            >
              Math courses
              {showMath
                ? education.mathCourses.map((c) => {
                    return <li>{c}</li>;
                  })
                : null}
            </ul>
          ) : null}
        </div>
      ) : null}
    </div>
  );
};

export default EduItem;
