import React, { useState } from "react";

function ExpItem({ experience }) {
  const [showDetails, setShowDetails] = useState(false);
  const [showCourses, setShowCourses] = useState(false);
  const [showMultipleEmployers, setShowMultipleEmployers] = useState(false);
  return (
    <div className="exp-item">
      <div className="exp-title">
        {experience.title}
        <span
          className="expand-button item cursor"
          onClick={() => setShowDetails(!showDetails)}
        >
          {showDetails ? "-" : "+"}
        </span>
      </div>
      <div className="exp-dates">
        <span className="exp-start">{experience.start}</span>
        {" - "}
        <span className="exp-end">{experience.end}</span>
      </div>
      <div className="exp-who">
        {typeof experience.forWho === "string" ? (
          experience.forWho
        ) : (
          <ul className="employers-list item-ul">
            {experience.forWho.map((e) => {
              return <li>{e}</li>;
            })}
          </ul>
        )}
      </div>
      {showDetails ? (
        <div className="exp-details">
          {experience.courses ? (
            <ul className="exp-courses item-ul">
              Courses Taught
              <span
                className="expand-button cursor"
                onClick={() => setShowCourses(!showCourses)}
              >
                {showCourses ? "-" : "+"}
              </span>
              {showCourses
                ? experience.courses.map((c) => {
                    return <li>{c}</li>;
                  })
                : null}
            </ul>
          ) : null}
          <ul>
            {experience.description
              ? experience.description.map((b) => {
                  return <li>{b}</li>;
                })
              : null}
          </ul>
        </div>
      ) : null}
    </div>
    // <div className="exp-item cursor">
    //   <div className="exp-title">{experience.title}</div>
    //   <span
    //     className="expand-button item cursor"
    //     onClick={() => setShowDetails(!showDetails)}
    //   >
    //     {showDetails ? "-" : "+"}
    //   </span>
    //   {showDetails ? (
    //     <div className="exp-details">
    //       <div className="exp-who">{experience.forWho}</div>
    //       <div className="exp-location">{experience.location}</div>
    //       <div className="exp-start">{experience.start}</div>
    //       <div className="exp-end">{experience.end}</div>
    //       <div className="exp-courses">{experience.courses}</div>
    //       <div className="exp-description">{experience.description}</div>
    //     </div>
    //   ) : null}
    // </div>
  );
}

export default ExpItem;
